import React, { useEffect } from 'react';
import Head from 'next/head';
import Link from 'next/link';
import { useRouter } from 'next/router';
import Sidebar from '../component/Sidebar';
// import Header from './Header';
import { useQuery } from '@apollo/client';
import { OBTENER_USUARIO } from '../graphql/usuarios';

const Layout = ({ children }) => {
  const router = useRouter();
  const { data, loading, error } = useQuery(OBTENER_USUARIO);
  const usuario = data?.obtenerUsuario;
  const shouldRedirect = !(loading || error || usuario);

  useEffect(() => {
    if (shouldRedirect) {
      router.push('/login');
    }
  }, [shouldRedirect]);

  if (error) return `Error || ${error.message}`;
  if (loading) return 'Cargando...';
  const { nombre, apellido } = usuario;
  // const Header = () => {

  //   return (
  //     <div className="sm:flex sm:justify-between mb-6">
  //       <p className="mr-2 mb-5 lg:mb-0 uppercase">
  //         Hola {nombre} {apellido}
  //       </p>

  //       <Link href="/cerrarsesion">
  //         <button
  //           type="button"
  //           className="bg-blue-800 w-full sm:w-auto font-bold uppercase text-xs rounded py-1 px-2 text-white shadow-md"
  //         >
  //           Cerrar Sesión
  //         </button>
  //       </Link>
  //     </div>
  //   );
  // };

  return (
    <>
      <Head>
        <title>CRM - Administracion de Clientes</title>
      </Head>
      {router.pathname === '/login' || router.pathname === '/registro' ? (
        <div className="bg-gray-800 min-h-screen flex flex-col justify-center">
          <div>{children}</div>
        </div>
      ) : (
        <div className="bg-gray-200 min-h-screen">
          <div className="sm:flex min-h-screen">
            <Sidebar />

            <main className="sm:w-2/3 xl:w-4/5 sm:min-h-screen p-5">
              <div className="sm:flex sm:justify-between mb-6">
                <p className="mr-2 mb-5 lg:mb-0 uppercase">
                  Hola {nombre} {apellido}
                </p>

                <Link href="/cerrarsesion">
                  <button
                    type="button"
                    className="bg-blue-800 w-full sm:w-auto font-bold uppercase text-xs rounded py-1 px-2 text-white shadow-md"
                  >
                    Cerrar Sesión
                  </button>
                </Link>
              </div>
              {children}
            </main>
          </div>
        </div>
      )}
    </>
  );
};

export default Layout;
