import Layout from '../component/Layout';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useMutation } from '@apollo/client';
import Swal from 'sweetalert2';
import { useRouter } from 'next/router';
import { NUEVO_PRODUCTO, OBTENER_PRODUCTOS } from '../graphql/productos';

export default function NuevoProducto() {
  const router = useRouter();
  const [nuevoProducto] = useMutation(NUEVO_PRODUCTO, {
    update(cache, { data: nuevoProducto }) {
      const { obtenerProductos } = cache.readQuery({
        query: OBTENER_PRODUCTOS,
      });

      cache.writeQuery({
        query: OBTENER_PRODUCTOS,
        data: {
          obtenerProductos: [...obtenerProductos, nuevoProducto],
        },
      });
    },
  });
  const initialValues = {
    codigo: '',
    nombre: '',
    existencia: '',
    precio: '',
    marca: '',
    undMed: '',
    presentacion: '',
  };
  const validationSchema = Yup.object().shape({
    codigo: Yup.string().required('El campo es obligatorio'),
    nombre: Yup.string().required('El campo es obligatorio'),
    existencia: Yup.number()
      .required('El campo es obligatorio')
      .positive('No se aceptan números negativos')
      .integer('La existencia deben ser números enteros'),
    precio: Yup.number()
      .required('El campo es obligatorio')
      .positive('No se aceptan números negativos'),
    marca: Yup.string().required('El campo es obligatorio'),
    undMed: Yup.string().required('El campo es obligatorio'),
    presentacion: Yup.string().required('El campo es obligatorio'),
  });

  const formik = useFormik({
    initialValues,
    validationSchema,
    onSubmit: async (values, helpers) => {
      const {
        codigo,
        nombre,
        existencia,
        precio,
        marca,
        undMed,
        presentacion,
      } = values;
      try {
        const input = {
          codigo,
          nombre,
          existencia,
          precio,
          marca,
          undMed,
          presentacion,
        };
        await nuevoProducto({ variables: { input } });

        Swal.fire('Creado', 'Se creó producto correctamente', 'success');
        helpers.setSubmitting(false);
        router.push('/productos');
      } catch (error) {
        console.log(error);
      }
    },
  });

  return (
    <Layout>
      <h1 className="text-2xl text-gray-800 font-light text-center mt-8">
        Crear Nuevo Producto
      </h1>

      <div className="flex justify-center mt-5">
        <div className="w-full max-w-lg">
          <form
            className="bg-white shadow-md px-8 pt-6 pb-8 mb-4 "
            onSubmit={formik.handleSubmit}
          >
            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="codigo"
              >
                Codigo
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="codigo"
                type="text"
                placeholder="Codigo de Producto"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.codigo}
              />
            </div>

            {formik.touched.codigo && formik.errors.codigo ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.codigo}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="nombre"
              >
                Nombre
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="nombre"
                type="text"
                placeholder="Nombre Producto"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.nombre}
              />
            </div>

            {formik.touched.nombre && formik.errors.nombre ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.nombre}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="existencia"
              >
                Cantidad Disponible
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="existencia"
                type="number"
                placeholder="Cantidad Disponible"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.existencia}
              />
            </div>

            {formik.touched.existencia && formik.errors.existencia ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.existencia}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="precio"
              >
                Precio
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="precio"
                type="number"
                placeholder="Precio Producto"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.precio}
              />
            </div>

            {formik.touched.precio && formik.errors.precio ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.precio}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="marca"
              >
                Marca
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="marca"
                type="text"
                placeholder="Marca"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.marca}
              />
            </div>

            {formik.touched.marca && formik.errors.marca ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.marca}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="undMed"
              >
                Unidad de Medida
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="undMed"
                type="text"
                placeholder="Unidad de Medida"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.undMed}
              />
            </div>

            {formik.touched.undMed && formik.errors.undMed ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.undMed}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="presentacion"
              >
                Presentacion
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="presentacion"
                type="text"
                placeholder="Presentacion"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.presentacion}
              />
            </div>

            {formik.touched.presentacion && formik.errors.presentacion ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.presentacion}</p>
              </div>
            ) : null}

            <input
              disabled={formik.isSubmitting || !formik.dirty}
              type="submit"
              className="bg-gray-800 w-full mt-5 p-2 text-white uppercase font-bold hover:bg-gray-900"
              value="Agregar Nuevo Producto"
            />
          </form>

          <pre>{JSON.stringify(formik.values, null, 2)}</pre>
          <pre>{JSON.stringify(formik.errors, null, 2)}</pre>
        </div>
      </div>
    </Layout>
  );
}
