import React, { useState, useEffect } from 'react';
import { useMutation } from '@apollo/client';
import {
  ACTUALIZAR_PEDIDO,
  ELIMINAR_PEDIDO,
  OBTENER_PEDIDOS_SINGLE,
} from '../../graphql/pedidos';
import Swal from 'sweetalert2';
import { useMessage } from '../../hooks/useMessage';

export default function Pedido({ pedido }) {
  const [mensaje, guardarMensaje, mostrarMensaje] = useMessage();
  const { id, total, cliente, estado } = pedido;
  const { nombre, mail, telefono } = cliente;
  const [actualizarPedido] = useMutation(ACTUALIZAR_PEDIDO);
  const [eliminarPedido] = useMutation(ELIMINAR_PEDIDO, {
    update(cache) {
      const { obtenerPedidos } = cache.readQuery({
        query: OBTENER_PEDIDOS_SINGLE,
      });

      cache.writeQuery({
        query: OBTENER_PEDIDOS_SINGLE,
        data: {
          obtenerPedidos: obtenerPedidos.filter((pedido) => pedido.id !== id),
        },
      });
    },
  });

  const [estadoPedido, setEstadoPedido] = useState(estado);
  const [clase, setClase] = useState('');
  useEffect(() => {
    if (estadoPedido) {
      setEstadoPedido(estadoPedido);
    }
    clasePedido();
  }, [estadoPedido]);
  const pedidoMap = pedido.pedido.map(({ __typename, ...order }) => order);
  console.log(pedidoMap);

  const clasePedido = () => {
    if (estadoPedido === 'PENDIENTE') {
      setClase('border-yellow-500');
    } else if (estadoPedido === 'PAGADO') {
      setClase('border-green-500');
    } else {
      setClase('border-blue-800');
    }
  };

  const cambiarEstadoPedido = async (nuevoEstado) => {
    try {
      const { data } = await actualizarPedido({
        variables: {
          id,
          input: { estado: nuevoEstado, cliente: cliente.id },
        },
      });
      setEstadoPedido(data.actualizarPedido.estado);
    } catch (error) {
      guardarMensaje(error.message.replace('Graphql error:', ''));

      setTimeout(() => {
        guardarMensaje(null);
      }, 3000);
    }
  };
  const confirmarEliminarPedido = () => {
    Swal.fire({
      title: '¿Deseas eliminar a este pedido?',
      text: 'Esta acción no se puede deshacer',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si, Eliminar',
      cancelButtonText: 'No, Cancelar',
    }).then(async (result) => {
      if (result.value) {
        try {
          const data = await eliminarPedido({
            variables: {
              id,
            },
          });

          Swal.fire('Eliminado', data.eliminarPedido, 'success');
        } catch (error) {
          guardarMensaje(error.message.replace('Graphql error:', ''));

          setTimeout(() => {
            guardarMensaje(null);
          }, 3000);
        }
      }
    });
  };

  return (
    <div
      className={` ${clase} border-t-4 mt-4 bg-white rounded p-6 md:grid md:grid-cols-2 md:gap-4 shadow-lg`}
    >
      {mensaje && mostrarMensaje()}
      <div>
        <p className="font-bold text-gray-800">Cliente: {nombre}</p>

        {mail && (
          <p className="flex items-center my-2">
            <svg
              fill="none"
              stroke="currentColor"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              viewBox="0 0 24 24"
              className="w-4 h-4 mr-2"
            >
              <path d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
            </svg>
            {mail}
          </p>
        )}

        {telefono && (
          <p className="flex items-center my-2">
            <svg
              fill="none"
              stroke="currentColor"
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              viewBox="0 0 24 24"
              className="w-4 h-4 mr-2"
            >
              <path d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z"></path>
            </svg>
            {telefono}
          </p>
        )}

        <h2 className="text-gray-800 font-bold mt-10">Estado Pedido:</h2>

        <select
          className="mt-2 appearance-none bg-blue-600 border border-blue-600 text-white p-2 text-center rounded leading-tight focus:outline-none focus:bg-blue-600 focus:border-blue-500 uppercase text-xs font-bold "
          value={estadoPedido}
          onChange={(e) => cambiarEstadoPedido(e.target.value)}
        >
          <option value="PENDIENTE">PENDIENTE</option>
          <option value="PAGADO">PAGADO</option>
          <option value="DESPACHADO">DESPACHADO</option>
        </select>
      </div>

      <div>
        <h2 className="text-gray-800 font-bold mt-2">Resumen del Pedido</h2>
        {pedido.pedido.map((articulo) => (
          <div key={articulo.id} className="mt-4">
            <p className="text-sm text-gray-600">
              Producto: {articulo.nombre}{' '}
            </p>
            <p className="text-sm text-gray-600">
              Cantidad: {articulo.cantidad}{' '}
            </p>
          </div>
        ))}

        <p className="text-gray-800 mt-3 font-bold ">
          Total a pagar:
          <span className="font-light"> $ {total}</span>
        </p>

        <button
          className="uppercase text-xs font-bold  flex items-center mt-4 bg-red-800 px-5 py-2 inline-block text-white rounded leading-tight"
          onClick={() => confirmarEliminarPedido()}
        >
          Eliminar Pedido
          <svg
            fill="none"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            className="w-4 h-4 ml-2"
          >
            <path d="M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z"></path>
          </svg>
        </button>
      </div>
    </div>
  );
}
