import { useRouter } from 'next/router';
import Layout from '../../component/Layout';
import { useQuery, useMutation } from '@apollo/client';
import { Formik } from 'formik';
import * as Yup from 'yup';
import Swal from 'sweetalert2';
import { OBTENER_CLIENTE, ACTUALIZAR_CLIENTE } from '../../graphql/clientes';

export default function EditarCliente() {
  const router = useRouter();
  const id = router.query.id;
  const { data, loading, error } = useQuery(OBTENER_CLIENTE, {
    variables: { id },
  });
  const [actualizarCliente] = useMutation(ACTUALIZAR_CLIENTE);

  const validationSchema = Yup.object().shape({
    nombre: Yup.string().required('El nombre del cliente es obligatorio'),
    apellido: Yup.string().required('El apellido del cliente es obligatorio'),
    empresa: Yup.string().required('El campo empresa  es obligatorio'),
    email: Yup.string()
      .email('Email no v√°lido')
      .required('El email del cliente es obligatorio'),
  });

  const handleSubmit = async (values, helpers) => {
    const { nombre, apellido, empresa, email, telefono } = values;
    const input = { nombre, apellido, empresa, email, telefono };
    try {
      await actualizarCliente({ variables: { id, input } });
      Swal.fire(
        'Actualizado',
        'El cliente se actualizo correctamente',
        'success'
      );
      helpers.setSubmitting(false);
      router.push('/');
    } catch (error) {
      const errorMessage = error.message.replace('Graphql error: ', '');
      Swal.fire('Error', errorMessage, 'error');
    }
  };

  if (loading) return 'Cargando...';
  if (error) return `Error || ${error.message}`;
  const { obtenerCliente } = data;

  return (
    <Layout>
      <h1 className="text-2xl text-gray-800 font-light">Editar Cliente</h1>

      <div className="flex justify-center mt-5">
        <div className="w-full max-w-lg">
          <Formik
            enableReinitialize
            initialValues={obtenerCliente}
            validationSchema={validationSchema}
            onSubmit={(valores) => {
              actualizarInfoCliente(valores);
            }}
          >
            {({
              values,
              errors,
              handleBlur,
              handleChange,
              handleSubmit,
              touched,
              isSubmitting,
            }) => {
              return (
                <form
                  className="bg-white shadow-md px-8 pt-6 pb-8 mb-4"
                  onSubmit={handleSubmit}
                >
                  <div className="mb-4">
                    <label
                      className="block text-gray-700 text-sm font-bold mb-2"
                      htmlFor="nombre"
                    >
                      Nombre
                    </label>

                    <input
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      id="nombre"
                      type="text"
                      placeholder="Nombre Cliente"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.nombre}
                    />
                  </div>

                  {touched.nombre && errors.nombre ? (
                    <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                      <p className="font-bold">Error</p>
                      <p>{errors.nombre}</p>
                    </div>
                  ) : null}

                  <div className="mb-4">
                    <label
                      className="block text-gray-700 text-sm font-bold mb-2"
                      htmlFor="apellido"
                    >
                      Apellido
                    </label>

                    <input
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      id="apellido"
                      type="text"
                      placeholder="Apellido Cliente"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.apellido}
                    />
                  </div>

                  {touched.apellido && errors.apellido ? (
                    <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                      <p className="font-bold">Error</p>
                      <p>{errors.apellido}</p>
                    </div>
                  ) : null}

                  <div className="mb-4">
                    <label
                      className="block text-gray-700 text-sm font-bold mb-2"
                      htmlFor="empresa"
                    >
                      Empresa
                    </label>

                    <input
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      id="empresa"
                      type="text"
                      placeholder="Empresa Cliente"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.empresa}
                    />
                  </div>

                  {touched.empresa && errors.empresa ? (
                    <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                      <p className="font-bold">Error</p>
                      <p>{errors.empresa}</p>
                    </div>
                  ) : null}

                  <div className="mb-4">
                    <label
                      className="block text-gray-700 text-sm font-bold mb-2"
                      htmlFor="email"
                    >
                      Email
                    </label>

                    <input
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      id="email"
                      type="email"
                      placeholder="Email Cliente"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.email}
                    />
                  </div>

                  {touched.email && errors.email ? (
                    <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                      <p className="font-bold">Error</p>
                      <p>{errors.email}</p>
                    </div>
                  ) : null}

                  <div className="mb-4">
                    <label
                      className="block text-gray-700 text-sm font-bold mb-2"
                      htmlFor="telefono"
                    >
                      Tel√©fono
                    </label>

                    <input
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      id="telefono"
                      type="tel"
                      placeholder="Tel√©fono Cliente"
                      onChange={handleChange}
                      onBlur={handleBlur}
                      value={values.telefono}
                    />
                  </div>

                  <input
                    disabled={isSubmitting}
                    type="submit"
                    className="bg-gray-800 w-full mt-5 p-2 text-white uppercase font-bold hover:bg-gray-900"
                    value="Editar Cliente"
                  />
                </form>
              );
            }}
          </Formik>
          ¬†
        </div>
      </div>
    </Layout>
  );
}
