import { OBTENER_CATEGORIAS } from '../graphql/categorias';
import { initializeApollo } from '../apollo/apolloClient';
import { useQuery } from '@apollo/client';
const Categorias = () => {
  const { data, loading, error } = useQuery(OBTENER_CATEGORIAS);
  if (loading || !data) return 'Cargando..';
  if (error) return `Error ${error.message}`;
  console.log(data);
  return <p>Desde Categorias</p>;
};

export const getStaticsProps = async (context) => {
  const client = initializeApollo();

  await client.query({ query: OBTENER_CATEGORIAS });

  return {
    props: { initializeApollo: client.cache.extract() },
  };
};

export default Categorias;
