import { OBTENER_CATEGORIAS } from '../graphql/categorias';
import { initializeApollo } from '../apollo/apolloClient';
import { useQuery } from '@apollo/client';
import { getStandAlone } from '../apollo';
const Categorias = (props) => {
  console.log(props);
  return <p>Desde Categorias</p>;
};

export const getStaticsProps = async (context) => {
  const client = getStandAlone();
  const categorias = client.query({ query: OBTENER_CATEGORIAS });

  return {
    props: { categorias },
  };
};

export default Categorias;
