import { OBTENER_CATEGORIAS } from '../graphql/categorias';
import { initializeApollo } from '../apollo/apolloClient';

export default function Categorias({ categorias }) {
  console.log(categorias);
  return <p>Desde Categorias</p>;
}

export const getStaticsProps = async (context) => {
  const client = initializeApollo();
  const categorias = await client.query({ query: OBTENER_CATEGORIAS });

  return {
    props: { categorias },
  };
};
