import { useState, useEffect, useMemo } from 'react';
import { useQuery } from '@apollo/client';
import { useRouter } from 'next/router';
import { OBTENER_USUARIO } from '../../graphql/usuarios';
import { UsuarioContext } from './UsuarioContex';

export const UsuarioState = ({ children }) => {
  const router = useRouter();
  const { data, loading, error } = useQuery(OBTENER_USUARIO);
  const value = useMemo(() => ({ data }), [data]);

  if (loading) return null;
  if (error) return null;
  if (!value) router.push('/login');

  return (
    <UsuarioContext.Provider value={value}>{children}</UsuarioContext.Provider>
  );
};
