import { OBTENER_CATEGORIAS } from '../graphql/categorias';
import { getStandAlone } from '../apollo';
import getApolloSate from '../apollo/apolloState';
import { useQuery } from '@apollo/client';
import { initializeApollo } from '../apollo/withApollo';
const Categorias = (props) => {
  const { data, loading, error } = useQuery(OBTENER_CATEGORIAS);
  if (loading) return 'Cargando..';
  if (error) return `Error ${error.message}`;

  return data.obtenerCategorias.map((categoria) => <h1>{categoria.nombre}</h1>);
};

export const getStaticsProps = async (ctx) => {
  const client = initializeApollo();
  client.query({ query: OBTENER_CATEGORIAS });

  return { props: { initialApolloState: client.cache.extract() } };
};

export default Categorias;
