import Layout from '../../../component/Layout';
import { useRouter } from 'next/router';
import { useMutation, useQuery } from '@apollo/client';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import Swal from 'sweetalert2';
import {
  OBTENER_PRODUCTO,
  ACTUALIZAR_PRODUCTO,
} from '../../../graphql/productos';

export default function EditarProducto(props) {
  console.log(props.hellow);
  const { data } = useQuery(OBTENER_PRODUCTO, {
    variables: { id },
  });
  const { obtenerProducto } = data;
  const [actualizarProducto] = useMutation(ACTUALIZAR_PRODUCTO);

  const initialValues = { obtenerProducto };
  const validationSchema = Yup.object().shape({
    nombre: Yup.string().required('Este campo es obligatorio'),
    existencia: Yup.number()
      .required('Este campo es obligatorio')
      .positive('No se aceptan números negativos')
      .integer('La existencia deben ser números enteros'),
    precio: Yup.number()
      .required('Este campo es obligatorio')
      .positive('No se aceptan números negativos'),
  });

  const formik = useFormik({
    enableReinitialize,
    initialValues,
    validationSchema,
    onSubmit: async (values, helpers) => {
      console.log(values);
      const { nombre, existencia, precio } = values;
      try {
        const input = { nombre, existencia, precio };
        await actualizarProducto({
          variables: { id, input },
        });
        helpers.setSubmitting(false);
        router.push('/productos');
        Swal.fire(
          'Correcto',
          'El producto se actualió correctamente',
          'success'
        );
      } catch (error) {
        console.log(error);
      }
    },
  });

  return (
    <Layout>
      <h1 className="text-2xl text-gray-800 font-light">Editar Producto</h1>

      <div className="flex justify-center mt-5">
        <div className="w-full max-w-lg">
          <form
            className="bg-white shadow-md px-8 pt-6 pb-8 mb-4"
            onSubmit={formik.handleSubmit}
          >
            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="nombre"
              >
                Nombre
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="nombre"
                type="text"
                placeholder="Nombre Producto"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.nombre}
              />
            </div>

            {formik.touched.nombre && formik.errors.nombre ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.nombre}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="existencia"
              >
                Cantidad Disponible
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="existencia"
                type="number"
                placeholder="Cantidad Disponible"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.existencia}
              />
            </div>

            {formik.touched.existencia && formik.errors.existencia ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.existencia}</p>
              </div>
            ) : null}

            <div className="mb-4">
              <label
                className="block text-gray-700 text-sm font-bold mb-2"
                htmlFor="precio"
              >
                Precio
              </label>

              <input
                className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                id="precio"
                type="number"
                placeholder="Precio Producto"
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                value={formik.values.precio}
              />
            </div>

            {formik.touched.precio && formik.errors.precio ? (
              <div className="my-2 bg-red-100 border-l-4 border-red-500 text-red-700 p-4">
                <p className="font-bold">Error</p>
                <p>{formik.errors.precio}</p>
              </div>
            ) : null}

            <input
              type="submit"
              className="bg-gray-800 w-full mt-5 p-2 text-white uppercase font-bold hover:bg-gray-900"
              value="Guardar Cambios"
            />
          </form>
        </div>
      </div>
    </Layout>
  );
}

EditarProducto.getInitalProps = async (ctx) => {
  console.log(ctx);

  return { message: 'Hellow' };
};
