import Link from 'next/link';
import Layout from '../component/Layout';
import { useQuery } from '@apollo/client';
import { OBTENER_CLIENTES_VENDEDOR } from '../graphql/clientes';
import { useRouter } from 'next/router';

const Index = () => {
  const { data, loading, error } = useQuery(OBTENER_CLIENTES_VENDEDOR);
  const router = useRouter();

  if (loading) return 'Cargando...';
  if (error) return `Error || ${error.message}`;
  if (!data.obtenerClientesVendedor) return router.push('/login');

  return (
    <Layout>
      <h1 className="text-2xl text-gray-800 font-light">Clientes</h1>

      <Link href="/nuevocliente">
        <a className="bg-blue-800 py-2 px-5 mt-3 inline-block text-white rounded text-sm hover:bg-gray-800 mb-3 uppercase font-bold w-full lg:w-auto text-center">
          Nuevo Cliente
        </a>
      </Link>
    </Layout>
  );
};

export default Index;
