import { OBTENER_CATEGORIAS } from '../graphql/categorias';
import { useQuery } from '@apollo/client';
import { getStandAloneApollo } from '../apollo';

function Categorias({ apolloCache }) {
  const { data, loading, error } = useQuery(OBTENER_CATEGORIAS);
  if (loading) return 'Cargando..';
  if (error) return `Error ${error.message}`;
  console.log(apolloCache);

  return data.obtenerCategorias.map((categoria) => (
    <ul>
      <li key={categoria.id}>{categoria.nombre}</li>
    </ul>
  ));
}

export async function getStaticProps(ctx) {
  const client = getStandAloneApollo();
  const categorias = await client.query({ query: OBTENER_CATEGORIAS });
  return {
    props: { categorias },
  };
}

export default Categorias;
