import { gql } from '@apollo/client';

export const OBTENER_CATEGORIAS = gql`
  query obtenerCategorias {
    obtenerCategorias {
      id
      nombre
    }
  }
`;
export const ELIMINAR_CATEGORIA = gql`
  query eliminarCategoria($id: ID!) {
    eliminarCategoria(id: $id)
  }
`;
